import React, {useContext} from 'react'
import { GlobalContext } from '../context/GlobalState';


export const Balance = ({transaction}) => {
    const {trancactions} = useContext(GlobalContext);
    const amounts = Trancactions.map(transaction => transaction.amount);
    const total = amounts.reduce((acc, item) => (acc+=item), 0).toFixed(2);

    return (
        <div>
            <h4>Your Balance</h4>
            <h1>${total}</h1>
        </div>
    )
}
